            int width = pitPattern.GetLength(1);
            int height = pitPattern.GetLength(0);
            int startX = centerX - width / 2;
            int startY = centerY - height / 2;

            for (int y = 0; y < height; y++)
            {
                for (int x = 0; x < width; x++)
                {
                    int worldX = startX + x;
                    int worldY = startY + y;

                    if (WorldGen.InWorld(worldX, worldY))
                    {
                        Tile tile = Framing.GetTileSafely(worldX, worldY);
                        if (pitPattern[y, x] == 0)
                        {
                            tile.HasTile = false;
                            tile.WallType = WallID.Sandstone;
                        }
                        else if (pitPattern[y, x] == 1)
                        {
                            tile.ResetToType(SandType);
                            tile.HasTile = true;
                            tile.WallType = WallID.Sandstone;
                        }
                        else if (pitPattern[y, x] == 2)
                        {
                            tile.ResetToType(SandType2);
                            tile.HasTile = true;
                            tile.WallType = WallID.Sandstone;
                        }
                    }
                }
            }

            WorldGen.SquareTileFrame(centerX, centerY, true);
            if (Main.netMode == NetmodeID.Server)
            {
                NetMessage.SendTileSquare(-1, centerX, centerY, Math.Max(width, height));
            }
